package com.chigo.architecturedemo.mvpimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport com.chigo.architecturedemo.Loginimport com.chigo.architecturedemo.Rimport kotlinx.android.synthetic.main.architecture.*/** * <pre> *     author : Chigo *     e-mail : isllyend@gmail.com *     time   : 2020/03/25 *     (: * </pre> */class MvpFragment : Fragment(), IView {    companion object {        fun newInstance(): Fragment {            val model: IModel = LoginModel();            val presenter: IPresenter = LoginPresenter();            val fragment = MvpFragment();            fragment.setPresenter(presenter);            model.setPresenter(presenter);            presenter.setModel(model);            presenter.setView(fragment);            return fragment;        }    }    private var loginPresenter: IPresenter? = null    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.architecture, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        tvTitle.text = "MVP"        btnConfirm.setOnClickListener {            loginPresenter?.login(Login(etAccount.text.toString(), etPwd.text.toString()))        }    }    override fun setPresenter(presenter: IPresenter) {        loginPresenter = presenter;    }    override fun showLoading() {        tvHandle.text = "login loading..."    }    override fun showResult(result: String) {        tvHandle.text = result    }}